import React, { useState, FC } from 'react'
import { MessageSchema } from '../api/entities/message/message.types'
import { useParams } from 'react-router-dom'
import img from '../icons/send_message.png'

export const Chat: FC = () => {
    const { userId, chatId } = useParams()

    const [messageList, setMessageList] = useState<MessageSchema[]>([])
    const [textareaValue, setTestareaValue] = useState<string>('')
  
    const handleChangeTextareaValue = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
        setTestareaValue(e.target.value)
    }

    const handleSendBoxClick = () => {
        if (userId && chatId) {
            const newMessage = {
                id: messageList.length + 1,
                title: textareaValue,
                userId: +userId,
                chatId: +chatId,
                createdAt: new Date()
            }
        
            setMessageList([...messageList, newMessage])
            setTestareaValue('')
        }
    }

    return (
        <div className='centered'>
            <div className='message-area'>
                {/* {messageList.length > 0 ? 
                    <>
                        {messageList.filter((message) => chatId && message.chatId === +chatId).map((message) => {
                            <div key={message.id} className={`message ${userId && message.userId === +userId ? 'right' : 'left'}`}>
                                {userId && message.userId === +userId ? <></> :<div className='message-sender'>`User ${message.userId}`</div>}
                                <div className='message-text'>{message.title}</div>
                                <div className='message-timestamp'>12:30</div>
                            </div>
                        })}
                    </> 
                    : 
                    <div className='no-messages'>Сообщений нет</div>
                } */}
                <div className={`message-row ${userId && 1 === +userId ? 'right' : 'left'}`}>
                    <div key={1} className={`message-cloud ${userId && 1 === +userId ? 'right' : 'left'}`}>
                        {userId && 1 === +userId ? <></> :<div className='message-sender'>{`User ${1}`}</div>}
                        <div className='message-text'>Hello</div>
                        <div className='message-timestamp'>12:30</div>
                    </div>
                </div>
                <div className={`message-row ${userId && 2 === +userId ? 'right' : 'left'}`}>
                    <div key={2} className={`message-cloud ${userId && 2 === +userId ? 'right' : 'left'}`}>
                        {userId && 2 === +userId ? <></> :<div className='message-sender'>{`User ${2}`}</div>}
                        <div className='message-text'>HellohsadhdhashdhsadHellohsadhdhashdhsadHellohsadhd hashdhsadHellohsadhdhashdhsadHellohsadhdhashdhsadHellohsadhdhashdhsad</div>
                        <div className='message-timestamp'>12:30</div>
                    </div>
                </div>
                <div className={`message-row ${userId && 2 === +userId ? 'right' : 'left'}`}>
                    <div key={2} className={`message-cloud ${userId && 2 === +userId ? 'right' : 'left'}`}>
                        {userId && 2 === +userId ? <></> :<div className='message-sender'>{`User ${2}`}</div>}
                        <div className='message-text'>Hello</div>
                        <div className='message-timestamp'>12:30</div>
                    </div>
                </div>
            </div>
            <div className='bottom-area'>
                <div className='input-area'>
                <textarea className='textarea' value={textareaValue} onChange={handleChangeTextareaValue} placeholder='Напишите сообщение...' rows={3} cols={67}></textarea>
                {textareaValue.length > 0 ? 
                    <img src={img} alt='img' className='send-box' onClick={handleSendBoxClick} />
                    : 
                    <></>
                }
                </div>
            </div>
        </div>
    )
}
