import React, { useEffect, FC } from 'react'
import { useParams } from 'react-router-dom'
import img from '../icons/send_message.png'
import { observer } from 'mobx-react-lite'
import { useStore } from '../hooks/use-store'
import { useAsyncEffect } from '../hooks/use-async-effect'

export const Chat: FC = observer(() => {
    const {messagesStore: {
        services: {
            textareaValue,
            handleSendBoxClick,
            handleChangeTextareaValue,
            setChatId,
            setUserId,
            updateMessageList
        },
        list
    }} = useStore()

    const { userId, chatId } = useParams()

    useEffect(() => {
        if (userId)
            setUserId(+userId)
        if (chatId)
            setChatId(+chatId)
    }, [userId, chatId])

    useAsyncEffect(() => {
        updateMessageList()
    }, [])

    return (
        <div className='centered'>
            <div className='message-area'>
                {list.length > 0 ? 
                    <>
                        {list.filter((message) => chatId && message.chatId === +chatId).map((message) => 
                            <div key={message.id} className={`message-row ${userId && message.userId === +userId ? 'right' : 'left'}`}>
                                <div className={`message-cloud ${userId && message.userId === +userId ? 'right' : 'left'}`}>
                                    {userId && message.userId === +userId ? 
                                        <></> 
                                        :
                                        <div className='message-sender'>{`User ${message.userId}`}</div>
                                    }
                                    <div>{message.title}</div>
                                    <div className='message-timestamp'>12:30</div>
                                </div>
                            </div>
                        )}
                    </> 
                    : 
                    <div className='no-messages'>Сообщений нет</div>
                }
            </div>
            <div className='bottom-area'>
                <div className='input-area'>
                <textarea className='textarea' value={textareaValue} onChange={handleChangeTextareaValue} placeholder='Напишите сообщение...' rows={3} cols={67}></textarea>
                {textareaValue.length > 0 ? 
                    <img src={img} alt='img' className='send-box' onClick={handleSendBoxClick} />
                    : 
                    <></>
                }
                </div>
            </div>
        </div>
    )
})
